                close_election()

                // change state to closed
                setState("closed")
            }
        }}>Close Election
    }

    // reopen button function(display to only contract deployer)
    const reopenButton = () => {
        // check if user is contract deployer
        if (window.accountId !== process.env.CONTRACT_NAME) {
            return
        }
        return  {
            let isSureToClose = confirm("Are you sure to reopen this election?");
            if (isSureToClose) {
                // reopen this election
                reopen_election()

                // change state to open
                setState("open")
            }
        }}>Reopen Election
    }

    // message to wait for fetching data
    const messageToWait = () => {
        return Fetching NFTs of candidates...
    }

    switch (state) {
        // in case fetching candidate NFTs info
        case State.Fetching:
            return {messageToWait()}

        // in case fetching number of likes for each candidate
        case State.Fetched:
            getCandidateLikes();
            return {messageToWait()}

        // in case all data is fetched(election is open)
        case State.Open:
            return (

                    {closeButton()}

                        {cardsInCaseOpen()}


            )

        // in case all data is fetched(election is closed)
        case State.Closed:
            return (

                    {reopenButton()}

                        {cardsInCaseClosed()}


            )
    }

}
export default Home;

このようにコントラクトから関数をインポートします。
import {
    nft_transfer,
    nft_add_likes_to_candidate,
    nft_tokens_for_kind,
    nft_return_candidate_likes,
    check_voter_has_been_added,
    check_voter_has_voted,
    voter_voted, close_election,
    if_election_closed,
    reopen_election
} from '../js/near/utils'

次に更新する変数を定義します。candidateInfoListは候補者の情報を入れるリスト、candidateLikesListはそれぞれの候補者の得票数を入れるリスト、stateはデータの取得・投票の締切の真偽の状態を管理するための変数です。
const [candidateInfoList, setCandidateInfoList] = useState();
const [candidateLikesList] = useState([]);
const [state, setState] = useState("fetching");

こちらは状態を管理するためのenum型という型で宣言されたものです。これによって描画のところでif文をいくつも使ってデータが取得できているかなどを確認する必要がなくなり可読性が上がります。
const State = {
    Fetching: "fetching",
    Fetched: "fetched",
    Open: "open",
    Closed: "closed"
}

ここではuseEffectでnft_tokens_for_kind関数を引数（candidate）をとることで候補者の情報を取得しています。
取得した値はsetCandidateInfoListによってcandidateInfoListというリストに格納されます。また、setStateによってstateという変数はfetchedに変わります。これによって次はそれぞれの候補者の得票数を取りにいくという次の段階に移ることができます。
useEffect(async () => {
    await nft_tokens_for_kind("candidate").then(value => {
        setCandidateInfoList(value);
        setState("fetched");
    });
}, [])

次のvote関数ではtokenのidを引数にしています。このtoken_idにはユーザーが取得した投票券のtokenのidが入ることになります。このtokenのidは投票する候補者のNFTのidが入ることになります。
const vote = (token_id) => {
    //check if user has already voted
    check_voter_has_voted(window.accountId).then(value => {
        if (Boolean(value)) {
            alert("You have already voted!")
            return
        }

        // check if user has vote ticket
        check_voter_has_been_added(window.accountId).then(value => {
            let tokenIdOfVoter = parseFloat(value);
            if (tokenIdOfVoter == 0) {
                alert("You don't have vote ticket! Please ask deployer to give it to you.")
                return
            }
            // confirm if user really vote to specified candidate(because even if they cancel transaction, contract judge user voted)
            let isSure = confirm("Once you vote, you can't change selected candidate. Are you OK?");
            if (!isSure) {
                return
            }
            // transfer vote ticket from user to contract(get rid of vote ticket)
            nft_transfer(process.env.CONTRACT_NAME, tokenIdOfVoter);
            // add vote to specified candidate
            nft_add_likes_to_candidate(token_id);

            //add user ID to voted-list
            voter_voted(window.accountId);
        })
    })
}

まずはcheck_voter_has_voted関数を走らせることでユーザーがすでに投票していないかをチェックします。投票済みであればalert()でそのことを知らせます。
引数として受け取っているwindow.accountIdとはサインインしているユーザーのwalletのidのことです。
check_voter_has_voted(window.accountId)

次にcheck_voter_has_been_added関数で投票者が投票券を保有しているかを確認します。ここで重要になってくるのがwalletのidと一緒に格納している値です。
check_voter_has_voted関数で確認のために使われるコントラクト内のベクターvoted_voter_listではwalletのidに紐づいて0という値を格納しています。なぜなら投票済みかどうかを確認するだけでいいからです。
一方でcheck_voter_has_been_added関数ではで確認のために使われるコントラクト内のベクターadded_voter_listではwalletのidに紐づいてそれぞれのユーザーに配布した投票券のidが返ってくるようになっています。これによって投票時にその投票券をコントラクトに返すnft_transfer関数を使うときにtokenのidを受け取ることができます。
もし投票券を持っていなければ投票券を配布してもらうようalert()でしらせます。
その後confirm()関数で1度投票したら2度とできないことを確認します。
check_voter_has_been_added(window.accountId)

cardsInCaseOpen関数では投票が開いているときの候補者それぞれのカードのUIを作成します。投票が閉じているときとの違いは後ほど説明します。
この中では取得した候補者の情報のリストcandidateInfoListの長さだけカードを作成しており、そのカードの下には投票ボタンをつけています。そのボタンを押すと先ほど説明したvote関数が走り投票が完了します。
最後にコントラクト内にある全ての候補者についてUIの作成が完了できたらそのリストを返し描画に反映されます。
const cardsInCaseOpen = () => {
    let candidateCardList = [];
    for (let i = 0; i 



                    {(candidateLikesList[i])}
                     vote(parseInt(event.target.value))} className="vote_button hover:skew-1">Vote!


        )
    }
    return candidateCardList
}

次のcardsInCaseClosed関数では投票が閉じているときの候補者のUIを作成しています。上のcardsInCaseOpen関数との違いは、投票による勝者が確定するのでその候補者の上にWin!という文字を載せて他の候補者についてはカードを薄くしています。
const cardsInCaseClosed = () => {
    let candidateCardList = [];
    let mostVotedNum = candidateLikesList.reduce((a, b) => { return Math.max(a, b) });
    // format data for rendering
    for (let i = 0; i 
                    Won!



                        {(candidateLikesList[i])}



            )
        } else {
            // for other candidate rendering
            candidateCardList.push(





                        {(candidateLikesList[i])}



            )
        }

    }
    return candidateCardList
}

選挙の勝者はmostVotedNumという変数にcandidateLikesListというリストの得票数の最大値をいれ、それと同値である候補者とそうでない候補者のUIを変えるようにしています。
let mostVotedNum = candidateLikesList.reduce((a, b) => { return Math.max(a, b) });

次のgetCandidateLikes関数ではnft_tokens_for_kind関数で取得したそれぞれの候補者のNFTの情報の内tokenのidを引数として受け取りnft_return_candidate_likes関数を呼ぶことで得票数を取得しています。
全候補者について得票数の取得が終了したらif_election_closed関数を呼ぶことで投票が終了しているかを確認して、それによってstateの値を変化させて実際に描画させるUIを変えています。
const getCandidateLikes = async () => {
    // get num of likes for each candidate
    for (let i = 0; i  {
            candidateLikesList.push(value);
        })
    }

    // check if election is closed
    let isClosed = await if_election_closed();
    console.log(isClosed);
    if (isClosed) {
        setState("closed");
    } else {
        setState("open")
    }
}

次のcloseButton関数はまず最初にコントラクトをdeployしたユーザーとサインインしているユーザーのwalletのidが一致していることを確認しています。誰でも締め切れるのは選挙として成り立ちませんからね。
それがクリアしていればボタンを表示するようにして、ボタンを押すとconfirm()で本当に投票を締め切るのかを確認してOKならばclose_election関数を呼ぶようにしています。
この後は投票が締め切っているようにしてUIを変更させます。
const closeButton = () => {
    // check if user is contract deployer
    if (window.accountId !== process.env.CONTRACT_NAME) {
        return
    }
    return  {
        // confirm that user really close this election
        let isSureToClose = confirm("Are you sure to close this election?");
        if (isSureToClose) {
